function createBtn(container, icone, game, page, row) {
  const btn = document.createElement("button");
  btn.classList.add("btn", "btn-primary", "ms-1");
  btn.setAttribute("type", "button");
  const i = document.createElement("i");
  i.classList.add("bi", icone);
  btn.appendChild(i);
  if (icone === "bi-arrows-fullscreen") {
    btn.setAttribute("data-bs-toggle", "modal");
    btn.setAttribute("data-bs-target", "#gameInoMadal");
  }
  if (icone === "bi-geo-alt") {
    btn.setAttribute("data-bs-toggle", "modal");
    btn.setAttribute("data-bs-target", "#gameLocationModal");
    btn.addEventListener("click", () => {
      gameLocationName.textContent = game.name;
    });
  }
  container.appendChild(btn);
  btn.addEventListener("click", (e) => {
    //DISPLAY game infos
    if (icone === "bi-arrows-fullscreen") {
      modalLabel.textContent = game.name;
      modalGameImg.setAttribute("src", game.image_url);
      modalPlayers.textContent = game.players;
      modalPlayTime.textContent = game.playtime + " min";
      modalBtn.className = "d-none";

      if (
        page === "WishList" &&
        !user.wishList.includes(game.id) &&
        !user.ludotheque.includes(game.id)
      ) {
        modalBtn.className = "btn btn-primary";
        modalIcone.className = "bi-heart";
        modalBtn.addEventListener("click", () => {
          addToWishList(
            user,
            game,
            gamesDropList,
            findGameForWishListInput,
            page
          );
        });
      }
      if (page === "Ludotheque" && !user.ludotheque.includes(game.id)) {
        modalBtn.className = "btn btn-primary";
        modalIcone.className = "bi-boxes";
        modalBtn.addEventListener("click", () => {
          addToLudotheque(
            user,
            game,
            gamesDropList,
            findGameForWishListInput,
            page,
            row
          );
        });
      }
    }
    //ADD to WishList
    if (icone === "bi-heart") {
      addToWishList(user, game, gamesDropList, findGameForWishListInput, page);
    }

    //ADD to Ludotheque
    if (icone === "bi-boxes") {
      addToLudotheque(
        user,
        game,
        gamesDropList,
        findGameForWishListInput,
        page,
        row
      );
    }

    //REMOVE
    if (icone === "bi-trash3") {
      if (page === "WishList") {
        const id = user.wishList.indexOf(game.id);
        user.wishList.splice(id, 1);
        row.remove();
      }
      if (page === "Ludotheque") {
        const id = user.ludotheque.indexOf(game.id);
        user.ludotheque.splice(id, 1);
        row.remove();
      }
    }
    //DISPLAY around game owner
    if (icone === "bi-geo-alt") {
      // data-bs-toggle="modal" data-bs-target="#gameLocationModal">
      btn;
      console.log(page + " : display the around " + game.name + " owners");
    }
  });
}
function getGameFromId(id) {
  return internalGameDB.find((elt) => elt.id === id);
}

function addToWishList(
  user,
  game,
  gamesDropList,
  findGameForWishListInput,
  page
) {
  if (!internalGameDB.includes(game)) internalGameDB.push(game);
  if (user.ludotheque.includes(game.id)) {
    alert(game.name + " est déjà dans votre Ludothèque");
    gamesDropList.innerHTML = "";
    findGameForWishListInput.value = "";
    console.log(game.name + " est déjà dans la LUDOTHEQUE");
  }
  if (user.wishList.includes(game.id)) {
    // ce jeu est déja dans la wishList
    alert("ce jeu est déjà dans votre WishList");
    gamesDropList.innerHTML = "";
    findGameForWishListInput.value = "";
    console.log(game.name + " est déjà dans la WIWHLIST");
  }
  // ce jeu n'est pas encore dans la wishList ni dans la ludotheque
  if (!user.wishList.includes(game.id) && !user.ludotheque.includes(game.id)) {
    user.wishList.push(game.id);
    gamesDropList.innerHTML = "";
    createDropListItem(
      page,
      game,
      ["displayAroundGameOwnerBtn", "addGameToLudothequeBtn", "removeGameBtn"],
      window.wishList
    );
    findGameForWishListInput.value = "";
  }
}

function addToLudotheque(
  user,
  game,
  gamesDropList,
  findGameForWishListInput,
  page,
  row
) {
  if (!internalGameDB.includes(game)) internalGameDB.push(game);
  if (user.ludotheque.includes(game.id)) {
    gamesDropList.innerHTML = "";
    findGameForWishListInput.value = "";
    alert(game.name + " est déjà dans votre Ludothèque");
    console.log(game.name + " est déjà dans la LUDOTHEQUE");
  }
  if (!user.ludotheque.includes(game.id)) {
    //AJOUT à LUDOTHEQUE
    user.ludotheque.push(game.id);
    gamesDropList.innerHTML = "";
    if (page === "Ludotheque")
      createDropListItem(page, game, ["removeGameBtn"], window.ludotheque);
    findGameForWishListInput.value = "";
    //REMOVE from WISHLIST
    if (user.wishList.includes(game.id)) {
      const wishListId = user.wishList.indexOf(game.id);
      user.wishList.splice(wishListId, 1);
      row.remove();
    }
  }
}

