function CreateBtn(label, color, line, cb) {
  const btn = document.createElement("btn");
  btn.classList.add("btn", `btn-${color}`);
  btn.textContent = label;
  line.appendChild(btn);
  btn.addEventListener("click", cb);
}

function modify(e) {
  const line = e.target.parentElement;
  const input = e.target.previousSibling;
  input.removeAttribute("disabled");
  input.focus();
  e.target.remove();
  CreateBtn("saugarder", "primary", line, save);
  CreateBtn("annuler", "secondary", line, cancel);
}

function cancel(e) {
  let userProperty;
  const line = e.target.parentElement;
  let input = e.target.previousSibling.previousSibling;
  const label =
    e.target.previousSibling.previousSibling.previousSibling.textContent;
  switch (label) {
    case "pseudo":
      userProperty = user.nickname;
      break;
    case "mail":
      userProperty = user.mail;
      break;
    case "adresse":
      userProperty = user.address;
      break;
  }
  input.value = userProperty;
  input.setAttribute("disabled", true);
  e.target.previousSibling.remove();
  e.target.remove();
  CreateBtn("modifier", "primary", line, modify);
}

function save(e) {
  const line = e.target.parentElement;
  let input = e.target.previousSibling;
  const label = e.target.previousSibling.previousSibling.textContent;
  switch (label) {
    case "pseudo":
      user.nickname = input.value;
      profilBtn.textContent = input.value;
      break;
    case "mail":
      user.mail = input.value;
      break;
    case "adresse":
      user.address = input.value;
      break;
  }
  input.setAttribute("disabled", true);
  e.target.nextSibling.remove();
  e.target.remove();
  CreateBtn("modifier", "primary", line, modify);
}

